import React from "react";
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  Col,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  Form,
  FormGroup,
  Input,
  Media,
  Pagination,
  PaginationItem,
  PaginationLink,
  Progress,
  Table,
  Container,
  Row,
  UncontrolledTooltip,  
  UncontrolledDropdown,

} from "reactstrap";
// core components
import Header from "components/Headers/Header.js";
import APIWarehouse from "./../../apis/APIWarehouse.js";
import APIPackage from "./../../apis/APIPackage.js";
import APIStorage from "./../../apis/APIStorage.js";
const serviceWarehouse = new APIWarehouse();
const serviceStorage = new APIStorage();
const servicePackage = new APIPackage();
const idWarehouseUser = sessionStorage.getItem("idAirport");
class Warehouse extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      infoPacks: [],        
      infoPack:[],  
      storage: 600,
      codeWarehouse: 1,
      packsNumber: 30,
      full: false,
      listPages: [],
      page: 1,
      pages: 0,
      iniPage: 0,
      finPage: 10,
      pageItems: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.findPackByCode = this.findPackByCode.bind(this);
    this.handleNextPage = this.handleNextPage.bind(this);
    this.handlePrevPage = this.handlePrevPage.bind(this);
    this.handleCurrentPage = this.handleCurrentPage.bind(this);
    this.checkout = this.checkout.bind(this);
  }
  
  handleChange = async (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
    
  };
  findPackByCode = async(event)=>{
    
  }
  checkout = async(idPack)=>{
    //confirmar salida del paquete
    const result = await serviceStorage.updateCheckOut(idPack,idWarehouseUser);
    //actualizar paquetes para ver el nuevo estado
    if(result["estado"].length<3){
      const resultPackages = await servicePackage.queryAllPackagesOutgoing(idWarehouseUser);    
      if (resultPackages["estado"].length < 3){
        let pageItems = resultPackages["resultado"].slice(
          this.state.iniPage,
          this.state.finPage
        );
        this.setState({
          infoPacks: resultPackages["resultado"],   
          pageItems: pageItems,
        });

      }
    }

  }
  componentWillMount = async () => {
    //primero se obtiene la información del almacén
     
    const resultWarehouse = await serviceWarehouse.getInfoWarehouse(idWarehouseUser);
    if (resultWarehouse["estado"].length<3){
      this.setState({
        storage:resultWarehouse["resultado"]["capacity"] ,
        codeWarehouse: resultWarehouse["resultado"]["id"],
        packsNumber: resultWarehouse["resultado"]["packagesNumber"],
        full: resultWarehouse["resultado"]["full"],
      })

    }
    //segundo se obtiene el grupo de paquetes del almacén
    const resultPackages = await servicePackage.queryAllPackagesOutgoing(idWarehouseUser);

    
    if (resultPackages["estado"].length < 3){
      let pageItems = resultPackages["resultado"].slice(
        this.state.iniPage,
        this.state.finPage
      );
      this.setState({
        infoPacks: resultPackages["resultado"],        
        pages: Math.ceil(resultPackages["resultado"].length / 10),
        pageItems: pageItems,
      });
    

    let listPages = [];
    for (let i = 0; i < this.state.pages; i++) {
      this.state.listPages.push(i + 1);
    }
    //console.log(this.state.pageItems);
  };
  }
  handlePrevPage = (event) => {
    //console.log(this.state.infoShipments);
    let pageItems = this.state.infoPacks.slice(
      this.state.iniPage - 10,
      this.state.finPage - 10
    );
    this.setState({
      page: this.state.page - 1,
      iniPage: this.state.iniPage - 10,
      finPage: this.state.finPage - 10,
      pageItems: pageItems,
    });
    //console.log(this.state.pageItems);
  };
  handleNextPage = (event) => {
    //console.log(this.state.infoShipments);
    let pageItems = this.state.infoPacks.slice(
      this.state.iniPage + 10,
      this.state.finPage + 10
    );
    this.setState({
      page: this.state.page + 1,
      iniPage: this.state.iniPage + 10,
      finPage: this.state.finPage + 10,
      pageItems: pageItems,
    });
    //console.log(this.state.pageItems);
  };
  handleCurrentPage = (event) => {
    let inicio = 0;
    let fin = 9;
    while (!(inicio <= event.target.value) && !(fin >= event.target.value)) {
      inicio = inicio + 10;
      fin = fin + 10;
    }
    let pageItems = this.state.infoPacks.slice(inicio, fin);
    this.setState({
      page: event.target.value,
      iniPage: inicio,
      finPage: fin,
      pageItems: pageItems,
    });
  };

  render() {
    

    let items = [];
    //console.log(this.state.pages);
    for (let number = 1; number <= this.state.pages; number++) {
      items.push(
        <PaginationItem
          value={number}
          className={this.state.page === number ? "active" : "disabled"}
        >
          <PaginationLink>{number}</PaginationLink>
        </PaginationItem>
      );
    }
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          {/* Table */}
          <Row>
            <div className="col">
              <Card className="shadow">
                <CardHeader className="border-0">
                  <Row>
                    <Col>
                      <h3 className="mb-0">Almacén</h3>
                    </Col>
                  </Row>
                  <br />
                  
                </CardHeader>
                <CardBody>
                <Form>
                <div className="pl-lg-4">
                    <Row>
                        <Col lg="6">
                            <FormGroup>
                            <label
                                className="form-control-label"
                                htmlFor="input-idWarehouse"
                            >
                                Código de almacén
                            </label>
                            <Input
                                disabled
                                className="form-control-alternative"
                                id="input-idWarehouse"
                                placeholder="Código de almacén"
                                type="text"
                                value={this.state.codeWarehouse}
                            />
                            </FormGroup>
                        </Col>
                        <Col lg="6">
                            <FormGroup>
                            <label
                                className="form-control-label"
                                htmlFor="input-numberPacks"
                            >
                                Número de paquetes
                            </label>
                            <Input
                                disabled
                                placeholder="Número de paquetes"
                                type="text"
                                value={this.state.packsNumber}   
                            />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col lg="6">
                            <FormGroup>
                            <label
                                className="form-control-label"
                                htmlFor="input-storage"
                            >
                                Capacidad
                            </label>
                            <Input
                                disabled
                                className="form-control-alternative"
                                id="input-storage"
                                placeholder="Capacidad"
                                type="text"
                                value={this.state.storage}
                            />
                            </FormGroup>
                        </Col>
                        <Col lg="6">
                            <FormGroup>
                            <label
                                className="form-control-label"
                                htmlFor="input-full"
                            >
                                Copado
                            </label>
                            <Input
                                disabled
                                id="input-full"
                                placeholder="Copado"
                                type="text"
                                value={this.state.full}   
                            />
                            </FormGroup>
                        </Col>
                    </Row>
                 </div>   
                </Form>
                <Table className="align-items-center table-flush" responsive>
                  <thead className="thead-light">
                    <tr>
                      <th scope="col">Código</th>
                      <th scope="col">Altura</th>
                      <th scope="col">Ancho</th>
                      <th scope="col">Largo</th>
                      <th scope="col">Peso</th>
                      <th scope="col">Frágil</th>
                      <th scope="col">Estado</th>
                      <th scope="col">Acciones</th>

                    </tr>
                  </thead>
                  <tbody>
                    {this.state.pageItems.map((pack) => {
                      return (
                        <tr>
                          <th>{pack["id"]}</th>
                          <td>{pack["high"]}</td>
                          <td>{pack["width"]}</td>
                          <td>{pack["large"]}</td>
                          <td>{pack["weight"]}</td>
                          <td>{pack["fragile"]}</td>
                          <td>{pack["status"]}</td>
                          <td>
                              <UncontrolledDropdown>
                                <DropdownToggle
                                  className="btn-icon-only text-light"
                                  href="#pablo"
                                  role="button"
                                  size="sm"
                                  color=""
                                  onClick={(e) => e.preventDefault()}
                                >
                                  <i className="fas fa-ellipsis-v" />
                                </DropdownToggle>
                                <DropdownMenu
                                  className="dropdown-menu-arrow"
                                  right
                                >
                                  <DropdownItem
                                    href="#pablo"
                                    onClick={() => this.checkout(pack["id"])}
                                  >
                                    Confirmar salida
                                  </DropdownItem>
                                </DropdownMenu>
                              </UncontrolledDropdown>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
                </CardBody>
                <CardFooter className="py-4">
                  <nav aria-label="...">
                    <Pagination
                      className="pagination justify-content-end mb-0"
                      listClassName="justify-content-end mb-0"
                    >
                      <PaginationItem
                        className={this.state.page - 1 == 0 ? "disabled" : ""}
                      >
                        <PaginationLink
                          onClick={this.handlePrevPage}
                          tabIndex="-1"
                        >
                          <i className="fas fa-angle-left" />
                          <span className="sr-only">Previous</span>
                        </PaginationLink>
                      </PaginationItem>
                      {items}
                      <PaginationItem
                        className={
                          this.state.page == this.state.pages ? "disabled" : ""
                        }
                      >
                        <PaginationLink onClick={this.handleNextPage}>
                          <i className="fas fa-angle-right" />
                          <span className="sr-only">Next</span>
                        </PaginationLink>
                      </PaginationItem>
                    </Pagination>
                  </nav>
                </CardFooter>
              </Card>
            </div>
          </Row>
        </Container>
      </>
    );
  }
}
export default Warehouse;
