import React from "react";
// node.js library that concatenates classes (strings)

// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Bar, Doughnut } from "react-chartjs-2";
import Slider from "@material-ui/core/Slider";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import ReactDatetime from "react-datetime";
// reactstrap components
import {
  Alert,
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col,
  Modal,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Spinner,
  ListGroup,
  ListGroupItem,
} from "reactstrap";
// core components

import { chartOptions, parseOptions,chartExample1, chartExample3} from "variables/charts.js";
import Header from "components/Headers/Header.js";
import APIShipment from "./../../apis/APIShipment.js";
import APISimulation from "./../../apis/APISimulation";
import APIIncident from "./../../apis/APIIncident.js";
const serviceTimeline = new APISimulation();
const serviceIncident = new APIIncident();

// Example 2 of Chart inside src/views/Index.js (Total orders - Card)
let chartExample2 = {
  options: {
    scales: {
      yAxes: [
        {
          ticks: {
            callback: function (value) {
              if (!(value % 10)) {
                //return '$' + value + 'k'
                return value;
              }
            },
          },
        },
      ],
    },
    tooltips: {
      callbacks: {
        label: function (item, data) {
          var label = data.datasets[item.datasetIndex].label || "";
          var yLabel = item.yLabel;
          var content = "";
          if (data.datasets.length > 1) {
            content += label;
          }
          content += yLabel;
          return content;
        },
      },
    },
  },
};
const useStyles = makeStyles({
  root: {
    width: 300,
  },
});

function valuetext(value) {
  return `${value}°C`;
}

class Simulation extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      messageConfirmation: [],
      fileSelection: [],
      infoModal: [],
      stateModal: false,
      loading: false,
      loaded: false,
      queryDate: null,
      labelsTimeLine: [],
      dataTimeLine: [],
      maxTimeLine: [],
      queryTimeline: false,
      dataChart1:[],
      labelsChart1:[],
      legendChart1:[],
      dataChart2:[],
      labelsChart2:[],
      legendChart2:[],
      dataChart3:{},
    };
    this.handleChange = this.handleChange.bind(this);
    this.submitFile = this.submitFile.bind(this);
    this.toggleModal = this.toggleModal.bind(this);
    this.handleChangeSimulation = this.handleChangeSimulation.bind(this);
    this.queryTimelineMoment = this.queryTimelineMoment.bind(this);
    if (window.Chart) {
      parseOptions(Chart, chartOptions());
    }
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
      messageConfirmation: [],
    });
  };
  handleChangeSimulation = (event, value) => {};

  toggleModal = () => {
    this.setState({
      stateModal: !this.state.stateModal,
    });
  };

  submitFile = async () => {
    //const fileInput = data.querySelector('#packs.generado.20200607') ;
    this.setState({ loading: true });
    const fileInput = document.querySelector("#input-fileSelectorPackages");
    const formData = new FormData();

    formData.append("file", fileInput.files[0]);

    //subir archivo

    var requestOptions = {
      method: "POST",
      body: formData,
      redirect: "follow",
    };
    console.log("procesando1");
    const uploadFileAns = await fetch(
      "http://54.146.196.79:8080/dp1/api/dispatch/upload/zip",
      requestOptions
    );

    const uploadFile = await uploadFileAns.json();
    console.log(uploadFile["resultado"]);

    if (uploadFile["estado"].length < 3) {
      let alertMessage = <Alert>El archivo se subió de manera correcta</Alert>;
      this.setState({
        messageConfirmation: alertMessage,
        loading: false,
        loaded: true,
      });

      this.toggleModal();
    } else {
      let alertMessage = (
        <Alert>El archivo no se subió de manera correcta</Alert>
      );

      this.setState({
        messageConfirmation: alertMessage,
        loading: false,
      });
    }
  };
  /*componentWillMount = async () => {
    const resultTimeline = await serviceTimeline.getTimeline(
      "20200203",
      "15:27"
    );
    console.log(resultTimeline["resultado"]);
    let dataChart1 = [],
      labelsChart1 = [];
    if (resultTimeline["estado"].length < 3) {
      resultTimeline["resultado"].map((airport) => {
        dataChart1.push(airport["percentage"]);
        labelsChart1.push(airport["code"]);
      });
      console.log(dataChart1);
      console.log(labelsChart1);
      this.setState({ dataTimeLine: dataChart1, labelsTimeLine: labelsChart1 });
    } else {
      this.setState({
        dataTimeLine: [0, 0, 0, 0, 0],
        labelsChart1: ["A1", "A2", "A3", "A4", "A5"],
      });
    }
  };*/
  componentWillMount = async()=>{
    const resultIncident = await serviceIncident.getDashboards();
    let dataChart1=[],labelsChart1=[],legendChart1=[],dataChart2=[],labelsChart2=[],legendChart2=[];
    if (resultIncident["estado"].length<3){
      resultIncident["resultado"]["byairports"].map((airport)=>{
          dataChart1.push(airport["count"]);
          labelsChart1.push(airport["code"]);
          legendChart1.push(airport["name"]);
      });
      resultIncident["resultado"]["byflightplans"].map((flight)=>{
          dataChart2.push(flight["count"]);
          labelsChart2.push(flight["idFlight"]);
          legendChart2.push([flight["codOrigin"],flight["codDestiny"]]);
      });
      console.log(dataChart1,labelsChart1,legendChart1);
      console.log(dataChart2,labelsChart2,legendChart2);
        this.setState({
            dataChart1:dataChart1,
            labelsChart1:labelsChart1,
            legendChart1:legendChart1,
            dataChart2:dataChart2,
            labelsChart2:labelsChart2,
            legendChart2:legendChart2,
            dataChart3:resultIncident["resultado"]["summaryCase"],
        })

    }
    else{
        //si hay error porque no hay data
      this.setState({
          dataChart1:[0,0,0,0,0],
          labelsChart1:["A1","A2","A3","A4","A5"],
          legendChart1:["N1","N2","N3","N4","N5"],
          dataChart2:[0,0,0,0,0],
          labelsChart2:["V1","V2","V3","V4","V5"],
          legendChart2:[["O1","D1"],["O2","D2"],["O3","D3"],["O4","D4"],["O5","D5"]],
          dataChart3:{"ok":0,"fails":0},
      })

    }

}
  queryTimelineMoment = async () => {
    console.log(this.state.queryDate.getFullYear());
    let year = this.state.queryDate.getFullYear();
    let month = this.state.queryDate.getMonth();
    if (month < 10) {
      month = "0" + (month + 1);
    }
    console.log(month);

    console.log(this.state.queryDate.getDate());
    let date = this.state.queryDate.getDate();
    if (date < 10) {
      date = "0" + date;
    }

    console.log(this.state.queryDate.getHours());
    let hours = this.state.queryDate.getHours();
    if (hours < 10) {
      hours = "0" + hours;
    }
    console.log(hours);

    console.log(this.state.queryDate.getMinutes());
    let min = this.state.queryDate.getMinutes();
    if (min < 10) {
      min = "0" + min;
    }
    let dateSend = year.toString() + month.toString() + date.toString();
    let hourSend = hours.toString() + ":" + min.toString();
    console.log(typeof dateSend);

    const resultTimeline = await serviceTimeline.getTimeline(
      dateSend,
      hourSend
    );
    console.log(resultTimeline["resultado"]);
    let dataChart1 = [],
      labelsChart1 = [];
    if (resultTimeline["estado"].length < 3) {
      resultTimeline["resultado"].map((airport) => {
        dataChart1.push(airport["percentage"]);
        labelsChart1.push(airport["code"]);
      });
      console.log(dataChart1);
      console.log(labelsChart1);
      this.setState({ dataTimeLine: dataChart1, labelsTimeLine: labelsChart1 });
    } else {
      this.setState({
        dataTimeLine: [0, 0, 0, 0, 0],
        labelsChart1: ["A1", "A2", "A3", "A4", "A5"],
      });
    }
    this.setState({ queryTimeline: true });
  };
  render() {
    for (let i = 0; i < this.state.dataTimeLine.length; i++) {
      this.state.maxTimeLine.push(100);
    }
    let mixedChart = {
      labels: this.state.labelsTimeLine,
      datasets: [
        {
          label: "% Ocupado: ",
          data: this.state.dataTimeLine,
        },
        {
          label: "% Máximo: ",
          data: this.state.maxTimeLine,
          type: "line",
        },
      ],
    };

    let data1={
      labels: this.state.labelsChart1,
      datasets: [
        {
          label: "Aeropuertos más ocupados",
          data: this.state.dataChart1,
          maxBarThickness: 10,
        },
      ],
    };
    let data2={
        labels: this.state.labelsChart2,
        datasets: [
          {
            label: "Vuelos más ocupados",
            data:this.state.dataChart2,
            maxBarThickness: 10,
          },
        ],
      };
    let data3={
        labels: ["Éxitos", "Fallos"],
        datasets: [
          {
            label: "Procesamiento",
            data: [
              this.state.dataChart3["ok"],
              this.state.dataChart3["fails"],
            ],
            maxBarThickness: 10,
          },
        ],

    };
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          {/* Form */}
          <Row>
            <Card className="bg-secondary shadow" style={{ width: "80rem" }}>
              <CardHeader className="bg-white border-0">
                <Row className="align-items-center">
                  <Col xs="10" className="col-sm">
                    <h3 className="mb-0">Simulación</h3>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Form>
                  <div className="pl-lg-4">
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-fileSelectorPackages"
                          >
                            Registro de paquetes
                          </label>
                          <div>
                            <Input
                              type="file"
                              name="fileSelection"
                              placeholder="Seleccionar archivo"
                              id="input-fileSelectorPackages"
                              onChange={(ev) => this.handleChange(ev)}
                            />
                          </div>
                        </FormGroup>
                      </Col>
                      <Col lg="6"></Col>
                    </Row>
                    <Row>{this.state.messageConfirmation}</Row>
                  </div>
                </Form>
                <Row>
                  <Col className="text-right">
                    <Button
                      color="primary"
                      type="button"
                      onClick={this.submitFile}
                    >
                      Subir
                    </Button>
                  </Col>
                </Row>
                <Row>
                  {this.state.loading && <Spinner color="primary" />}
                </Row>                
                  <div>
                    <div className="modal-header">
                      <h2>Resultado de la simulación</h2>
                    </div>                   
                  </div>
                
                <br />
                <div>
                  {this.state.queryTimeline && (
                    <Col lg="9">
                      <div className="chart">
                        <Bar
                          data={mixedChart}
                          options={chartExample2.options}
                        />
                      </div>
                    </Col>
                  )}
                  <FormGroup style={{ width: "30%" }}>
                    <label className="form-control-label">Fecha</label>
                    <InputGroup className="input-group-alternative">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="ni ni-calendar-grid-58" />
                        </InputGroupText>
                      </InputGroupAddon>
                      <ReactDatetime
                        inputProps={{
                          placeholder: "Seleccionar fecha",
                        }}
                        timeFormat={true}
                        onChange={(e) => {
                          this.setState({ queryDate: new Date(e) });
                          console.log(new Date(e));
                        }}
                      />
                      <InputGroupAddon addonType="prepend">
                        <Button
                          className="btn-icon btn-2"
                          color="primary"
                          outline
                          type="button"
                          onClick={this.queryTimelineMoment}
                        >
                          <span className="btn-inner--icon">
                            <i className="ni ni-zoom-split-in" />
                          </span>
                        </Button>
                      </InputGroupAddon>
                    </InputGroup>
                  </FormGroup>

                    <Row>
                        <h3>TOP 5 Almacenes ocupados</h3>
                    </Row>
                    <Row>
                        <Col lg="9">
                            <div className="chart">
                                <Bar data={data1} options={chartExample3.options} />
                            </div>  
                        </Col>
                        <Col lg="3">
                            <ListGroup>
                                {this.state.legendChart1.map((airport)=>{
                                    return(
                                    <ListGroupItem>{airport}</ListGroupItem>
                                    )
                                })}
                            </ListGroup>

                        </Col>                    
                    </Row>
                    <Row>
                        <h3>TOP 5 Vuelos ocupados</h3>
                    </Row>
                    <Row>
                        <Col lg="9">
                            <div className="chart">
                                <Bar data={data2} options={chartExample3.options} />
                            </div>
                        </Col>
                        <Col lg="3">
                            <ListGroup>
                                {this.state.legendChart2.map((flight)=>{
                                    return(
                                    <ListGroupItem>{flight[0]}-{flight[1]}</ListGroupItem>
                                    )
                                })}
                            </ListGroup>
                        </Col> 
                    </Row>
                    <Row>
                        <h3>Resumen</h3>
                    </Row>
                    <Row>
                        <Col lg="9">
                            <div className="chart">
                                <Doughnut data={data3}  />
                            </div>
                        </Col>
                        <Col lg="3">
                            <ListGroup>
                                <ListGroupItem>Casos de éxito</ListGroupItem>
                                <ListGroupItem>Casos de fallo</ListGroupItem>
                            </ListGroup>
                        </Col>
                    </Row>

                  
                  {/*<Typography id="discrete-slider" gutterBottom>
                      Fecha
                    </Typography>
                    <Slider
                      style={{ width: 80 + "%" }}
                      defaultValue={0}
                      getAriaValueText={valuetext}
                      aria-labelledby="discrete-slider"
                      valueLabelDisplay="auto"
                      step={10}
                      marks
                      min={0}
                      max={50}
                      onChange={this.handleChangeSimulation}
                    />
                    <Typography id="discrete-slider" gutterBottom>
                      Hora
                    </Typography>
                    <Slider
                      style={{ width: 80 + "%" }}
                      defaultValue={0}
                      getAriaValueText={valuetext}
                      aria-labelledby="discrete-slider"
                      valueLabelDisplay="auto"
                      step={10}
                      marks
                      min={0}
                      max={50}
                      onChange={this.handleChangeSimulation}
                />*/}
                </div>  
                            
              </CardBody>
            </Card>
          </Row>
        </Container>
      </>
    );
  }
}
export default Simulation;
