import React from "react";
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col,
  Pagination,
  PaginationItem,
  PaginationLink,
  Table,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
} from "reactstrap";
// core components
import Header from "components/Headers/Header.js";
import APITravelPlan from "./../../../apis/APITravelPlan";

class ShipmentStep4 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      routePlan: [],
      //foundRoute: false,
    };
    this.closeNewShipment = this.closeNewShipment.bind(this);
    this.returnPreviousStep = this.returnPreviousStep.bind(this);
  }

  closeNewShipment = () => {
    this.props.history.push({
      pathname: "/admin/shipments",
    });
  };
  returnPreviousStep = () => {
    this.props.history.push({
      pathname: "/admin/addShipmentStep3",
    });
  };
  componentWillMount = async () => {
    const routeService = new APITravelPlan();
    const idStart = sessionStorage.getItem("idAirport");
    const routePlan = await routeService.generateRoute(
      idStart,
      this.props.location.state["destAirport"]
    );
    console.log(
      routePlan["resultado"]["flightPlans"]["0"]["flight"]["takeOffAirport"][
        "description"
      ]
    );
    if (routePlan["estado"].length < 3) {
      //se tienen los pedidos
      this.setState({
        routePlan: routePlan["resultado"]["flightPlans"],
      });
    }
  };

  render() {
    //console.log(this.props.location.state["destAirport"]);
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          {/*Table*/}
          <Row>
            <Card className="bg-secondary shadow" style={{ width: "80rem" }}>
              <CardHeader className="bg-white border-0">
                <Row className="align-items-center">
                  <Col xs="10" className="col-sm">
                    <h3 className="mb-0">4) Resultados del plan de vuelo </h3>
                  </Col>
                  <Col xs="8" className="col-sm">
                    <nav aria-label="...">
                      <Pagination
                        className="pagination justify-content-center mb-0"
                        listClassName="justify-content-end mb-0"
                      >
                        <PaginationItem>
                          <PaginationLink>1</PaginationLink>
                        </PaginationItem>
                        <PaginationItem>
                          <PaginationLink>2</PaginationLink>
                        </PaginationItem>
                        <PaginationItem>
                          <PaginationLink>3</PaginationLink>
                        </PaginationItem>
                        <PaginationItem className="active">
                          <PaginationLink>4</PaginationLink>
                        </PaginationItem>
                      </Pagination>
                    </nav>
                  </Col>
                  <Col className="col-sm text-right" xs="4">
                    <Button
                      className="btn-icon btn-2"
                      color="primary"
                      type="button"
                      onClick={this.closeNewShipment}
                    >
                      <span className="btn-inner--icon">
                        <i className="fas fa-times-circle" />
                      </span>
                    </Button>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Table className="align-items-center table-flush" responsive>
                  <thead className="thead-light">
                    <tr>
                      <th scope="col">COD. VUELO</th>
                      <th scope="col">ORIGEN</th>
                      <th scope="col">DESTINO</th>
                      <th scope="col">FECHA Y HORA DE DESPEGUE</th>
                      <th scope="col">FECHA Y HORA DE ATERRIZAJE</th>
                      <th scope="col">CIUDAD Y PAÍS ORIGEN</th>
                      <th scope="col">CIUDAD Y PAÍS DESTINO</th>
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.routePlan.map((flight) => {
                      return (
                        <tr key={flight["id"]}>
                          <td></td>
                          <td>
                            {flight["flight"]["takeOffAirport"]["description"]}
                          </td>
                          <td>
                            {flight["flight"]["arrivalAirport"]["description"]}
                          </td>
                          <td>{flight["flight"]["takeOffTime"]}</td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
                <Row>
                  <Col className="text-left">
                    <Button
                      color="primary"
                      outline
                      type="button"
                      onClick={this.returnPreviousStep}
                    >
                      Regresar
                    </Button>
                  </Col>
                  <Col className="text-right">
                    <Button
                      color="primary"
                      type="button"
                      onClick={this.closeNewShipment}
                    >
                      Guardar
                    </Button>
                  </Col>
                </Row>
              </CardBody>
            </Card>
          </Row>
        </Container>
      </>
    );
  }
}
export default ShipmentStep4;
