import React from "react";
// reactstrap components
import {
    Alert,
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Container,
    Row,
    Col,
    InputGroupAddon,
    InputGroupText,
    InputGroup,
  } from "reactstrap";
  // core components
  import Header from "components/Headers/Header.js";
class Simulation extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            messageConfirmation: [],   
            fileSelection:[],
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange=(event)=>{
        this.setState({
            fileSelection:event.target.value,

        })
        console.log(event.target.value);
    }



    render() {
        
        return (
            <>
            <Header/>
            <Container className="mt--7" fluid>
                {/* Form */}
                <Row>
                    <Card className="bg-secondary shadow" style={{ width: "80rem" }}>
                        <CardHeader className="bg-white border-0">
                            <Row className="align-items-center">
                            <Col xs="10" className="col-sm">
                                <h3 className="mb-0">Simulación de capacidad </h3>
                            </Col>                            
                            </Row>
                        </CardHeader>
                        <CardBody>
                            <Form>
                            <div className="pl-lg-4">
                                <Row>
                                <Col lg="6">
                                    <FormGroup>
                                    <label
                                        className="form-control-label"
                                        htmlFor="input-fileSelectorPackages"
                                    >
                                        Registro de paquetes
                                    </label>
                                    <Input
                                        type="file" 
                                        name="file" 
                                        placeholder="Seleccionar archivo"
                                        id="input-fileSelectorPackages"
                                        style={{color:"#5e72e4"}}
                                        onChange={(ev)=>this.handleChange(ev)}
                                    />
                                    </FormGroup>
                                </Col>
                                <Col lg="6">
                                    
                                </Col>
                                </Row>   
                                <Row>
                                {this.state.messageConfirmation}
                                </Row>
                            </div>
                            </Form>
                            
                        </CardBody>
                    </Card>
                </Row>
            </Container>
            </>
        )
    }
}  
export default Simulation;
