import React from "react";
// reactstrap components
import {
  Button,
  Badge,
  Card,
  CardHeader,
  CardFooter,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Modal,
  Table,
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  InputGroupAddon,
  InputGroupText,
  Input,
  InputGroup,
  Label,
} from "reactstrap";
// core components
import Header from "components/Headers/Header.js";
//apis
import APIShipment from "./../../../apis/APIShipment.js";
import APICountry from "apis/APICountry.js";
import APICity from "apis/APICountry.js";

class Shipment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stateModal: false,
      infoModal: [],
      infoShipments: [],
      trackingCode: 0,
      foundShipment: false,
      infoShipment: [],
      idCityFilter: 0,
      idCountryFilter: 0,
      countries: [],
      cities: [],
    };
    this.newShipment = this.newShipment.bind(this);
    this.showRoutes = this.showRoutes.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.findShipmentByTrackingCode = this.findShipmentByTrackingCode.bind(this);
    this.finishShipment = this.finishShipment.bind(this);
    this.filterData = this.filterData.bind(this);
  }

  newShipment = () => {
    this.props.history.push({
      pathname: "/admin/addShipmentStep1",
    });
  };
  showRoutes = (flightPlan) => {
    this.setState({
      stateModal: !this.state.stateModal,
      infoModal: flightPlan,
    });
  };
  handleChange = async(event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
    //si el cambio es del país se obtienen ciudades
    let nameState = event.target.name;
    let idCountry = event.target.name;
    if(nameState.localeCompare("idCountryFilter")==0){
      const cityService = new APICity();
      let idCity = sessionStorage.getItem("idCityAirport");
      const citiesA = await cityService.queryCitiesByIdCountry(
        idCountry, idCity
      );
      let cities = [];
      if (citiesA["estado"].length<3){
        //si hay ciudades
        cities.push(citiesA["resultado"]);
      }
      this.setState({ cities: cities });
    }
  };
  findShipmentByTrackingCode = async () => {
    const shipmentService = new APIShipment();
    const infoShipment = await shipmentService.findShipmentByTrackingCode(
      this.state.trackingCode
    );
    const message = infoShipment["estado"];
    if (message.length < 3) {
      //Ok
      this.setState({
        infoShipment: infoShipment["resultado"],
        foundShipment: true,
      });
    } else {
      this.setState({
        foundShipment: false,
      });
    }
  };
  finishShipment = async (trackingCode)=>{
    const shipmentService = new APIShipment();
    const infoShipments = await shipmentService.finishShipmentNow('FINALIZADO',trackingCode);   
    if(this.state.foundShipment && infoShipments["estado"].length<3){

      this.setState({infoShipment:infoShipments["resultado"]})
    }
    else if (infoShipments["estado"].length<3){
      let idAirport = sessionStorage.getItem("idAirport");
      let infoShipments = await shipmentService.listShipments(idAirport);
      this.setState({infoShipments:infoShipments["resultado"]})

    }


  }
  filterData =()=>{
    


  }
  componentWillMount = async () => {
    const shipmentService = new APIShipment();
    const countryService = new APICountry();
    const idAirport = sessionStorage.getItem("idAirport");
    const infoShipments = await shipmentService.listShipments(idAirport);
    const countries =  await countryService.queryAllCountries();

    if (infoShipments["estado"].length < 3) {
      //se tienen los pedidos
      this.setState({
        infoShipments: infoShipments["resultado"],
      });
      
    }
    if (countries["estado"].length<3){
      this.setState({
        countries: countries["resultado"],
      });

    }
  };

  render() {
    let optionCountries;
    let optionCities;
    if (this.state.countries.length > 0) {
      optionCountries = this.state.countries.map((country) => (
        <option value={country["id"]}>{country["name"]}</option>
      ));
    }
    if (this.state.cities.length > 0) {
      optionCities = this.state.cities.map((city) => (
        <option value={city["id"]}>{city["name"]}</option>
      ));
    }

    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          {/* Tabla */}
          <Row>
            <div className="col">
              <Card className="shadow">
                <CardHeader className="border-0">
                  <Row>
                    <Col >
                      <h3 className="mb-0">Registro de envíos</h3>
                    </Col>
                    <Col >
                      <Form>
                        <FormGroup>
                          <InputGroup className="mb-4 justify-content-center">
                            <Input
                              placeholder="Buscar por código de rastreo"
                              type="text"
                              name="trackingCode"
                              //value={this.state.trackingCode}
                              onChange={(ev) => {
                                this.handleChange(ev);
                              }}
                            />
                            <InputGroupAddon addonType="prepend">
                              <Button
                                className="btn-icon btn-2"
                                color="primary"
                                outline
                                type="button"
                                onClick={this.findShipmentByTrackingCode}
                              >
                                <span className="btn-inner--icon">
                                  <i className="ni ni-zoom-split-in" />
                                </span>
                              </Button>
                            </InputGroupAddon>
                          </InputGroup>
                        </FormGroup>
                      </Form>
                    </Col>
                    <Col className="text-right"  xs="4">
                      <Button
                        className="btn-icon btn-2"
                        color="primary"
                        type="button"
                        onClick={this.newShipment}
                      >
                        <span className="btn-inner--icon">
                          <i className="fas fa-plus-circle" />
                        </span>
                      </Button>
                    </Col>
                  </Row>
                  <Form>
                  <Row>
                    {/*Filtros*/ }
                    
                    <Col lg="2">                      
                      <FormGroup>
                        <label className="form-control-label" htmlFor="input-filterCountry"> 
                            País destino
                        </label>                          
                        <Input
                          placeholder="País destino"
                          type="select"
                          name="idCountryFilter"
                          id = "input-filterCountry"
                          value={this.state.idCountryFilter}
                          onChange={(ev) => {this.handleChange(ev)}}
                        >
                        <option value={0}>Selecciona un país</option>  
                        {optionCountries}
                        </Input>                         
                      </FormGroup>                      
                    </Col>
                    <Col lg="2"> 
                      <FormGroup>
                        <label className="form-control-label" htmlFor="input-filterCity">
                          Ciudad destino
                        </label>                          
                        <Input
                          placeholder="Ciudad destino"
                          type="select"
                          name="idCityFilter"
                          id = "input-filterCity"
                          value={this.state.idCityFilter}
                          onChange={(ev) => {this.handleChange(ev)}}
                        >
                        <option value={0}>Selecciona una ciudad</option>
                        {optionCities}
                        </Input> 
                      </FormGroup>
                    </Col>
                    <Col>
                      {/*filtrar por fechas*/ }
                    </Col>
                    <Col className="col-sm text-right" lg="2">
                      <br/>
                      <Button
                        color="primary"
                        type="button"
                        onClick={this.filterData}
                      >
                        Filtrar
                      </Button>
                    </Col>
                    
                  </Row>
                  </Form>
                </CardHeader>
                <Table className="align-items-center table-flush" responsive>
                  <thead className="thead-light">
                    <tr>
                      <th scope="col">COD. RASTREO</th>
                      <th scope="col">PAÍS DESTINO</th>
                      <th scope="col">CIUDAD DESTINO</th>
                      <th scope="col">AEROPUERTO DESTINO</th>
                      <th scope="col">CLIENTE EMISOR</th>
                      <th scope="col">CLIENTE RECEPTOR</th>
                      <th scope="col">FECHA REGISTRO</th>
                      <th scope="col">HORA REGISTRO</th>
                      <th scope="col">ESTADO</th>                      
                      <th scope="col">ACCIONES</th>
                      <th scope="col" />
                    </tr>
                  </thead>
                  <tbody>
                    {!this.state.foundShipment ? (
                      this.state.infoShipments.map((shipment) => {
                        return (
                          <tr key={shipment["id"]}>
                            <th>{shipment["trackingCode"]}</th>
                            <td>
                              {shipment["destinationAirport"]["city"]["country"]["name"]}
                            </td>
                            <td>
                              {shipment["destinationAirport"]["city"]["name"]}
                            </td>
                            <td>
                              {shipment["destinationAirport"]["description"]}
                            </td>
                            <td>
                              {shipment["send_client"]["name"]}{" "}
                              {shipment["send_client"]["lastname"]}
                            </td>
                            <td>
                              {shipment["receiveClientName"]}{" "}
                              {shipment["receiveClientLastname"]}
                            </td>
                            <td>{shipment["registerDate"].substring(0,10)}</td>
                            <td>{shipment["registerDate"].substring(11,16)}</td>
                            <td>{shipment["status"]}</td>                            
                            <td>
                              <UncontrolledDropdown>
                                <DropdownToggle
                                  className="btn-icon-only text-light"
                                  href="#pablo"
                                  role="button"
                                  size="sm"
                                  color=""
                                  onClick={(e) => e.preventDefault()}
                                >
                                  <i className="fas fa-ellipsis-v" />
                                </DropdownToggle>
                                <DropdownMenu
                                  className="dropdown-menu-arrow"
                                  right
                                >
                                  <DropdownItem
                                    href="#pablo"
                                    onClick={() =>
                                      this.showRoutes(
                                        shipment["pack"]["routePlan"][
                                          "flightPlans"
                                        ]
                                      )
                                    }
                                  >
                                    Ver ruta
                                  </DropdownItem>
                                  <DropdownItem
                                    href="#pablo"
                                    onClick={() =>this.finishShipment(shipment["trackingCode"])}
                                  >
                                    Finalizar
                                  </DropdownItem>
                                </DropdownMenu>
                              </UncontrolledDropdown>
                            </td>
                          </tr>
                        );
                      })
                    ) : ( //información de un solo envío para la búsqueda
                      <tr>
                        <th>{this.state.infoShipment["trackingCode"]}</th>
                        <td>
                          {this.state.infoShipment["destinationAirport"]["city"]["country"]["name"]}
                        </td>
                        <td>
                          {this.state.infoShipment["destinationAirport"]["city"]["name"]}
                        </td>
                        <td>
                          {this.state.infoShipment["destinationAirport"]["description"]}
                        </td>
                        <td>
                          {this.state.infoShipment["send_client"]["name"]}{" "}
                          {this.state.infoShipment["send_client"]["lastname"]}
                        </td>
                        <td>
                          {this.state.infoShipment["receiveClientName"]}{" "}
                          {this.state.infoShipment["receiveClientLastname"]}
                        </td>
                        <td>{this.state.infoShipment["registerDate"].substring(0,10)}</td>
                        <td>{this.state.infoShipment["registerDate"].substring(11,16)}</td>
                        <td>{this.state.infoShipment["status"]}</td>                        
                        <td>
                          <UncontrolledDropdown>
                            <DropdownToggle
                              className="btn-icon-only text-light"
                              href="#pablo"
                              role="button"
                              size="sm"
                              color=""
                              onClick={(e) => e.preventDefault()}
                            >
                              <i className="fas fa-ellipsis-v" />
                            </DropdownToggle>
                            <DropdownMenu className="dropdown-menu-arrow" right>
                              <DropdownItem
                                href="#pablo"
                                onClick={() =>
                                  this.showRoutes(
                                    this.state.infoShipment["pack"][
                                      "routePlan"
                                    ]["flightPlans"]
                                  )
                                }
                              >
                                Ver ruta
                              </DropdownItem>
                              <DropdownItem
                                href="#pablo"
                                onClick={() =>this.finishShipment(this.state.infoShipment["trackingCode"])}
                              >
                                Finalizar
                              </DropdownItem>
                            </DropdownMenu>
                          </UncontrolledDropdown>
                        </td>
                      </tr>
                    )}
                  </tbody>
                </Table>
                <Modal
                  className="modal-dialog-centered"
                  isOpen={this.state.stateModal}
                  toggle={() => this.showRoutes([])}
                >
                  <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">
                      Ruta
                    </h5>
                    <button
                      aria-label="Close"
                      className="close"
                      data-dismiss="modal"
                      type="button"
                      onClick={() => this.showRoutes([])}
                    >
                      <span aria-hidden={true}>×</span>
                    </button>
                  </div>
                  <div className="modal-body">
                    <Table
                      className="align-items-center table-flush"
                      responsive
                    >
                      <thead className="thead-light">
                        <tr>
                          <th scope="col">COD. VUELO</th>
                          <th scope="col">ORIGEN</th>
                          <th scope="col">DESTINO</th>
                          <th scope="col">FECHA Y HORA DE DESPEGUE</th>
                          <th scope="col">FECHA Y HORA DE ATERRIZAJE</th>
                          <th scope="col">CIUDA/PAÍS ORIGEN</th>
                          <th scope="col">CIUDA/PAÍS DESTINO</th>
                          <th scope="col" />
                        </tr>
                      </thead>
                      <tbody>
                        {this.state.infoModal.map((flightPlan) => (
                          <tr key={flightPlan["id"]}>
                            <th scope="col">
                              {flightPlan["flight"]["idFlight"]}
                            </th>
                            <td scope="col">
                              {flightPlan["flight"]["takeOffAirport"]["code"]}
                            </td>
                            <td scope="col">
                              {flightPlan["flight"]["arrivalAirport"]["code"]}
                            </td>
                            <td scope="col">
                              {flightPlan["takeOffDate"]}{" "}
                              {flightPlan["flight"]["takeOffTime"]}
                            </td>
                            <td scope="col">
                              {flightPlan["arrivalDate"]}{" "}
                              {flightPlan["flight"]["arrivalTime"]}
                            </td>
                            <td scope="col">
                              {flightPlan["flight"]["takeOffAirport"]["city"]["name"] }
                              ,{" "}
                              {flightPlan["flight"]["takeOffAirport"]["city"]["country"]["name"] }
                            </td>
                            <td scope="col">
                              {flightPlan["flight"]["arrivalAirport"]["city"]["name"] }
                              ,{" "}
                              { flightPlan["flight"]["arrivalAirport"]["city"]["country"]["name"]}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </div>
                  <div className="modal-footer">
                    <Button
                      color="primary"
                      data-dismiss="modal"
                      type="button"
                      onClick={() => this.showRoutes([])}
                    >
                      Cerrar
                    </Button>
                  </div>
                </Modal>
              </Card>
            </div>
          </Row>
        </Container>
      </>
    );
  }
}
export default Shipment;
