import React from "react";
// reactstrap components
import {
  Button,
  Badge,
  Card,
  CardHeader,
  CardFooter,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Table,
  Container,
  Row,
  Col,
  UncontrolledTooltip,
  Form,
  FormGroup,
  InputGroupAddon,
  InputGroupText,
  Input,
  InputGroup,
  CardBody,
} from "reactstrap";
// core components
import Header from "components/Headers/Header.js";
class Client extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      documentSearch:'',
      documentC: '',
      idC: 0,
      nameC: '',
      lastnameC: '',
      emailC: '',
      cellphoneC: '',
      foundClient: false,
    };
    this.findClientByDocument = this.findClientByDocument.bind(this);
    this.saveInfoClient = this.saveInfoClient.bind(this);
    this.handleChangeDocumentSearch = this.handleChangeDocumentSearch.bind(this);
    this.handleChangeIdC = this.handleChangeIdC.bind(this);
    this.handleChangeDocumentC = this.handleChangeDocumentC.bind(this);
    this.handleChangeNameC = this.handleChangeNameC.bind(this);
    this.handleChangeLastnameC = this.handleChangeLastnameC.bind(this);
    this.handleChangeEmailC = this.handleChangeEmailC.bind(this);
    this.handleChangeCellphoneC = this.handleChangeCellphoneC.bind(this);
  }
  handleChangeDocumentSearch = (event)=>{
    this.setState({
      documentSearch: event.target.value,
    });
  }
  handleChangeDocumentC = (event) => {
    this.setState({
      documentC: event.target.value,
    });
  };
  handleChangeIdC = (event) => {
    this.setState({
      idC: event.target.value,
    });
  };
  handleChangeNameC = (event) => {
    this.setState({
      nameC: event.target.value,
    });
  };
  handleChangeLastnameC = (event) => {
    this.setState({
      lastnameC: event.target.value,
    });
  };
  handleChangeEmailC = (event) => {
    this.setState({
      emailC: event.target.value,
    });
  };
  handleChangeCellphoneC = (event) => {
    this.setState({
      cellphoneC: event.target.value,
    });
  };

  findClientByDocument = () =>{
    //buscar cliente 

  }

  saveInfoClient = () => {
    //edit information of the client
    
  };


  render() {
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          {/* Client Information */}
          <Row>
            <div className="col">
              <Card className="shadow">
                <CardHeader className="border-0">
                  <Row>
                    <Col>
                      <h3 className="mb-0">Información del cliente</h3>
                    </Col>
                    <Col>
                      <Form>
                        <FormGroup>
                          <InputGroup className="mb-4">
                            <Input
                                placeholder="Buscar"
                                type="text"
                                value={this.state.documentSearch}
                                onChange={(ev) => {
                                  this.handleChangeDocumentSearch(ev);
                                }}
                            />
                            <InputGroupAddon addonType="prepend">
                                <Button
                                  className="btn-icon btn-2"
                                  color="primary"
                                  outline
                                  type="button"
                                  onClick={this.findClientByDocument}
                                >
                                  <span className="btn-inner--icon">
                                    <i className="ni ni-zoom-split-in" />
                                  </span>
                                </Button>
                            </InputGroupAddon>
                          </InputGroup>
                        </FormGroup>
                      </Form>
                    </Col>
                    <Col className="text-right" xs="4">
                      <Button
                        className="btn-icon btn-2"
                        color="primary"
                        type="button"
                        onSubmit={this.saveInfoClient}                        
                      >
                        <span className="btn-inner--icon">
                          <i className="far fa-save" />
                        </span>
                      </Button>
                    </Col>
                  </Row>
                </CardHeader>
                <CardBody>
                <Form>
                  <div className="pl-lg-4">
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-idClient"
                          >
                            Código de cliente
                          </label>
                          <Input
                            disabled
                            className="form-control-alternative"                            
                            id="input-idClient"
                            placeholder="Código cliente"
                            type="text"
                            value={this.state.idC}
                          />
                        </FormGroup>
                      </Col>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-document"
                          >
                            Número de documento
                          </label>
                          <Input 
                              className="form-control-alternative"
                              placeholder="Número de documento" 
                              id="input-document"
                              type="text"
                              value={this.state.documentC}
                              onChange ={(ev) => {
                                this.handleChangeDocumentC(ev);
                              }}
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-first-name"
                          >
                            Nombre
                          </label>
                          <Input
                            className="form-control-alternative"                            
                            id="input-first-name"
                            placeholder="Nombre"
                            type="text"
                            value ={this.state.nameC}
                            onChange ={(ev) => {
                              this.handleChangeNameC(ev);
                            }}
                          />
                        </FormGroup>
                      </Col>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-last-name"
                          >
                            Apellidos
                          </label>
                          <Input
                            className="form-control-alternative"                            
                            id="input-last-name"
                            placeholder="Apellidos"
                            type="text"
                            value ={this.state.lastnameC}
                            onChange ={(ev) => {
                              this.handleChangeLastnameC(ev);
                            }}
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-email"
                          >
                            Correo electrónico
                          </label>
                          <Input
                            className="form-control-alternative"
                            id="input-email"
                            placeholder="Correo Electrónico"
                            type="email"
                            value ={this.state.emailC}
                            onChange ={(ev) => {
                              this.handleChangeEmailC(ev);
                            }}
                          />
                        </FormGroup>
                      </Col>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-number"
                          >
                            Número telefónico
                          </label>
                          <Input
                            className="form-control-alternative"                            
                            id="input-number"
                            placeholder="Número telefónico"
                            type="text"
                            value ={this.state.cellphoneC}
                            onChange ={(ev) => {
                              this.handleChangeCellphoneC(ev);
                            }}
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                  </div>
                </Form>
                </CardBody>
              </Card>
            </div>
          </Row>
        </Container>
      </>
    );
  }
}
export default Client;
