import React from "react";
// reactstrap components
import {
    Button,
    Card,
    CardHeader,
    CardFooter,
    Col,
    Form,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,    
    Media,
    Pagination,
    PaginationItem,
    PaginationLink,
    Progress,
    Table,
    Container,
    Row,
    UncontrolledTooltip
  } from "reactstrap";
  // core components
  import Header from "components/Headers/Header.js";
  import APIAirport from "./../../apis/APIAirport.js";
  const serviceAirport = new APIAirport();

class Flight extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            infoFlights: [],
            infoFlight: [],
            flightCode: 0,
            foundFlight: false,

        };
        this.handleChange = this.handleChange.bind(this);
        this.findFlightByCode = this.findFlightByCode.bind(this);


    }
    
    findFlightByCode = ()=>{
        //Buscar vuelo por c贸digo de vuelo
       /* const infoFlight = await serviceAirport.findFligth(
            this.state.flightCode
          );
          const message = infoFlight["estado"];
          if (message.length < 3) {
            //Ok
            this.setState({
              infoFlight: infoFlight["resultado"],
              foundFlight: true,
            });
          } else {
            this.setState({
              foundFlight: false,
            });
          }*/



    }
    handleChange = async(event)=>{
        this.setState({
            [event.target.name]: event.target.value,
          });
    }

    componentWillMount= async()=>{
        const resultFlights = await serviceAirport.queryAllFlights();
        if (resultFlights["estado"].length<3)
            this.setState({infoFlights: resultFlights["resultado"]});
     
    }

    render() {
        
        return (
            <>
            <Header/>
            <Container className="mt--7" fluid>
                {/* Table */}
                <Row>
                    <div className="col">
                    <Card className="shadow">
                        <CardHeader className="border-0">
                        <Row>
                            <Col>
                            <h3 className="mb-0">Registro de vuelos</h3>
                            </Col>
                            <Col>
                            <Form>
                                <FormGroup>
                                <InputGroup className="mb-4 justify-content-center">
                                    <Input
                                    placeholder="Buscar por c贸digo de vuelo"
                                    type="text"
                                    name="flightCode"
                                    //value={this.state.trackingCode}
                                    onChange={(ev) => {
                                        this.handleChange(ev);
                                    }}
                                    />
                                    <InputGroupAddon addonType="prepend">
                                    <Button
                                        className="btn-icon btn-2"
                                        color="primary"
                                        outline
                                        type="button"
                                        onClick={this.findFlightByCode}
                                    >
                                        <span className="btn-inner--icon">
                                        <i className="ni ni-zoom-split-in" />
                                        </span>
                                    </Button>
                                    </InputGroupAddon>
                                </InputGroup>
                                </FormGroup>
                            </Form>
                            </Col>
                        </Row>
                        </CardHeader>
                        <Table className="align-items-center table-flush" responsive>
                        <thead className="thead-light">
                            <tr>
                            <th scope="col">C贸digo de vuelo</th>
                            <th scope="col">Origen</th>
                            <th scope="col">Destino</th>
                            <th scope="col">Hora de despegue</th>
                            <th scope="col">Hora de aterrizaje</th>
                            <th scope="col">Capacidad</th>
                            
                            </tr>
                        </thead>
                        <tbody>
                            {!this.state.foundFlight?
                             this.state.infoFlights.map((flight)=>{
                               return(
                                    <tr>
                                        <th>{flight["idFlight"]}</th>
                                        <td>{flight["takeOffAirport"]["description"]}</td>
                                        <td>{flight["arrivalAirport"]["description"]}</td>
                                        <td>{flight["takeOffTime"]}</td>
                                        <td>{flight["arrivalTime"]}</td>
                                        <td>{flight["capacity"]}</td>
                                    </tr>
                               );
                             })
                             :
                             ""
                            }
                            
                        </tbody>
                        </Table>
                    </Card>
                    </div>
                </Row>
            </Container>
            </>
        )
    }
}  
export default Flight;
