import React from "react";
// reactstrap components
import {
  Alert,
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col,
  Modal,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
} from "reactstrap";
// core components
import Header from "components/Headers/Header.js";
import APIShipment from "./../../apis/APIShipment.js";
class Simulation extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      messageConfirmation: [],
      fileSelection: [],
      infoModal: [],
      stateModal: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.submitFile = this.submitFile.bind(this);
    this.toggleModal = this.toggleModal.bind(this);

  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
      messageConfirmation: [],
    });
  };

  toggleModal = () => {
    this.setState({
      stateModal: !this.state.stateModal
    })  

  }

  
  submitFile = async () => {
    //const fileInput = data.querySelector('#packs.generado.20200607') ;
    const fileInput = document.querySelector("#input-fileSelectorPackages");
    const formData = new FormData();

    formData.append("file", fileInput.files[0]);

    //console.log( fileInput.files[0], formData);

    var requestOptions = {
      method: "POST",
      body: formData,
      redirect: "follow",
    };
    console.log("procesando1");
    const uploadFileAns = await fetch(
      "http://54.146.196.79:8080/dp1/api/dispatch/upload/zip",
      requestOptions
    );
    console.log("procesando2");
    const uploadFile = await uploadFileAns.json();
    console.log(uploadFile["resultado"]);

    if (uploadFile["estado"].length < 3) {
      let infoModal = <div>
                      <Row>
                          <h4>Fallos: {uploadFile["resultado"]["failed"]}</h4>
                      </Row>
                      <Row>
                          <h4>Éxitos: {uploadFile["resultado"]["ok"]}</h4>
                      </Row>
                      </div>;
      
      let alertMessage = <Alert>El archivo se subió de manera correcta</Alert>;
      this.setState({ messageConfirmation: alertMessage,                     
                      infoModal: infoModal});
      
      this.toggleModal();

      
    } else {
      let alertMessage = <Alert>El archivo no se subió de manera correcta</Alert>;

      this.setState({ messageConfirmation: alertMessage ,
                      infoModal: [] });
    }


  };

  render() {
    return (
      <>
        <Header />
        <Container className="mt--7" fluid>
          {/* Form */}
          <Row>
            <Card className="bg-secondary shadow" style={{ width: "80rem" }}>
              <CardHeader className="bg-white border-0">
                <Row className="align-items-center">
                  <Col xs="10" className="col-sm">
                    <h3 className="mb-0">Simulación de capacidad </h3>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Form>
                  <div className="pl-lg-4">
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-fileSelectorPackages"
                          >
                            Registro de paquetes
                          </label>
                          <div>
                            <Input
                              type="file"
                              name="fileSelection"
                              placeholder="Seleccionar archivo"
                              id="input-fileSelectorPackages"
                              onChange={(ev) => this.handleChange(ev)}
                            />
                          </div>
                        </FormGroup>
                      </Col>
                      <Col lg="6"></Col>
                    </Row>
                    <Row>{this.state.messageConfirmation}</Row>
                  </div>
                </Form>
                
                <Row>
                  <Col className="text-right">
                    <Button
                      color="primary"
                      type="button"
                      onClick={this.submitFile}
                    >
                      Subir
                    </Button>
                    <Modal
                      className="modal-dialog-centered"
                      isOpen={this.state.stateModal}
                      toggle={() => this.toggleModal()}
                    >
                      <div className="modal-header">
                        <h3 className="modal-title" id="exampleModalLabel">
                          Resultado de la simulación
                        </h3>
                        <button
                          aria-label="Close"
                          className="close"
                          data-dismiss="modal"
                          type="button"
                          onClick={() => this.toggleModal()}
                        >
                          <span aria-hidden={true}>×</span>
                        </button>
                      </div>
                      <div className="modal-body">
                        {this.state.infoModal}                        
                      </div>
                      <div className="modal-footer">
                        <Button
                          color="primary"
                          data-dismiss="modal"
                          type="button"
                          onClick={() => this.toggleModal()}
                        >
                          Cerrar
                        </Button>
                      </div>
                    </Modal>
                  </Col>
                </Row>
              </CardBody>
            </Card>
          </Row>
        </Container>
      </>
    );
  }
}
export default Simulation;
