import React from "react";
// reactstrap components
import {
    Alert,
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Container,
    Row,
    Col,
    InputGroupAddon,
    InputGroupText,
    InputGroup,
  } from "reactstrap";
  // core components
  import Header from "components/Headers/Header.js";
  import APIShipment from "./../../apis/APIShipment.js";
class Simulation extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            messageConfirmation: [],   
            fileSelection:[],
        }
        this.handleChange = this.handleChange.bind(this);
        this.submitFile = this.submitFile.bind(this);
    }

    handleChange=(event)=>{
        this.setState({[event.target.name]:event.target.value,
                        messageConfirmation: [],
                       })
        
    }
    submitFile = async()=>{

        const shipmentService = new APIShipment();
        //const fileInput = data.querySelector('#packs.generado.20200607') ;
        const formData = new FormData();    
        formData.append('file[0]',this.state.fileSelection );
        console.log(formData);
        const uploadFile = await shipmentService.uploadZip(formData);
        if (uploadFile["estado"].length<3){
            let alertMessage = <Alert>El archivo se subió de manera correcta</Alert>
            this.setState({messageConfirmation:alertMessage});
        }
        else{
            this.setState({messageConfirmation:[]});
        }
    }



    render() {
        
        return (
            <>
            <Header/>
            <Container className="mt--7" fluid>
                {/* Form */}
                <Row>
                    <Card className="bg-secondary shadow" style={{ width: "80rem" }}>
                        <CardHeader className="bg-white border-0">
                            <Row className="align-items-center">
                            <Col xs="10" className="col-sm">
                                <h3 className="mb-0">Simulación de capacidad </h3>
                            </Col>                            
                            </Row>
                        </CardHeader>
                        <CardBody>
                            <Form>
                            <div className="pl-lg-4">
                                <Row>
                                <Col lg="6">
                                    <FormGroup>
                                    <label
                                        className="form-control-label"
                                        htmlFor="input-fileSelectorPackages"
                                    >
                                        Registro de paquetes
                                    </label>
                                    <div >
                                        <Input
                                            type="file" 
                                            name="fileSelection" 
                                            placeholder="Seleccionar archivo"
                                            id="input-fileSelectorPackages"                                    
                                            onChange={(ev)=>this.handleChange(ev)}
                                        />
                                        
                                    </div>
                                    </FormGroup>
                                </Col>
                                <Col lg="6">
                                    
                                </Col>
                                </Row>   
                                <Row>
                                {this.state.messageConfirmation}
                                </Row>
                            </div>
                            </Form>
                            <Row>
                                <Col className="text-right">
                                    <Button
                                    color="primary"
                                    type="button"
                                    onClick={this.submitFile}
                                    >
                                    Subir
                                    </Button>
                                </Col>
                            </Row>
                            
                        </CardBody>
                    </Card>
                </Row>
            </Container>
            </>
        )
    }
}  
export default Simulation;
