/*!

=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import { Route, Switch, Redirect } from "react-router-dom";
// reactstrap components
import { Container } from "reactstrap";
// core components
import AdminNavbar from "components/Navbars/AdminNavbar.js";
import AdminFooter from "components/Footers/AdminFooter.js";
import Sidebar from "components/Sidebar/Sidebar.js";

import routes from "routes.js";
const roleUser = parseInt(sessionStorage.getItem("roleUser"), 10);
var arrayRoutes=[]
routes.map((route)=>{
  if(route.role===0 || route.role===roleUser)
     arrayRoutes.push(route);
});
class Admin extends React.Component {
  componentDidUpdate(e) {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
    this.refs.mainContent.scrollTop = 0;
    
  }
  getRoutes = arrayRoutes => {
    return arrayRoutes.map((prop, key) => {
      if (prop.layout === "/admin" /*&& (prop.role===0 || prop.role===roleUser)*/) {
        return (
          <Route
            path={prop.layout + prop.path}
            component={prop.component}
            key={key}
          />
        );
      } else {
        return null;
      }
    });
  };
  getBrandText = path => {
    for (let i = 0; i < arrayRoutes.length; i++) {
      if (
        this.props.location.pathname.indexOf(
          arrayRoutes[i].layout + arrayRoutes[i].path
        ) !== -1
      ) {
        return arrayRoutes[i].name;
      }
    }
    return "Brand";
  };

  render() {
    
    return (
      <>
        <Sidebar
          {...this.props}
          routes={arrayRoutes}
          logo={{
            innerLink: "/admin/index",
            imgSrc: require("assets/img/brand/logoRedExTraceTools.png"),
            imgAlt: "..."
          }}
        />
        <div className="main-content" ref="mainContent">
          <AdminNavbar
            {...this.props}
            brandText={this.getBrandText(this.props.location.pathname)}
          />
          <Switch>
            {this.getRoutes(arrayRoutes)}
            <Redirect from="*" to="/admin/index" />
          </Switch>
          <Container fluid>
            <AdminFooter />
          </Container>
        </div>
      </>
    );
  }
}

export default Admin;
